package com.yavuzmobile.movie.feature.homeimport androidx.compose.foundation.layout.*import androidx.compose.material.Scaffoldimport androidx.compose.runtime.Composableimport androidx.compose.runtime.collectAsStateimport androidx.compose.runtime.getValueimport androidx.compose.ui.Alignmentimport androidx.compose.ui.Modifierimport androidx.compose.ui.unit.dpimport androidx.hilt.navigation.compose.hiltViewModelimport androidx.navigation.NavHostControllerimport com.google.accompanist.pager.*@OptIn(ExperimentalPagerApi::class)@Composablefun HomeScreen(    navController: NavHostController,    viewModel: HomeViewModel = hiltViewModel()) {    viewModel.fetchMovieNowPlaying()    viewModel.fetchMovieUpComing()    val sliderItems by viewModel.sliderUIState.collectAsState()    val movieUpComingList by viewModel.homeListUiState.collectAsState()    Scaffold { paddingValues ->        Column(            Modifier                .fillMaxSize()                .padding(paddingValues)        ) {            val sliderPagerState = rememberPagerState()            Spacer(modifier = Modifier.height(16.dp))            MovieNowPlayingSlider(items = sliderItems, pagerState = sliderPagerState)            HorizontalPagerIndicator(                pagerState = sliderPagerState,                modifier = Modifier                    .align(Alignment.CenterHorizontally)                    .padding(16.dp),            )            MovieUpComingList(                navController,                movieUpComingList,                Modifier.fillMaxSize()            )        }    }}